<?php/*! * Concerto 1.0 beta * http://themeconcert.com/concerto * * [WARNING] * This is restricted file and should not be modified in any way(unless you know what * you are doing). This file serves as the Base Class for setting up administration pages. */class ConcertoAdmin {	public function __construct() {		//Require the individual Administration Pages		require_once CONCERTO_ADM . 'general.php';		require_once CONCERTO_ADM . 'design.php';		require_once CONCERTO_ADM . 'tools.php';		require_once CONCERTO_ADM . 'support.php';				//Bind Concerto to Wordpress		add_action('admin_init', array($this, 'init'));		add_action('admin_head', array($this, 'head'));		add_action('admin_menu', array($this, 'menu'));	}		public function init() {		$stylesheet_directory = get_bloginfo('stylesheet_directory');		wp_enqueue_script('jQueryMasonry', $stylesheet_directory . '/core/scripts/jquery.masonry.js', null, CONCERTO_VERSION);	}		public function head() {		?>		<!-- Concerto Framework Files -->		<link rel="stylesheet" type="text/css" href="<?php bloginfo('stylesheet_directory'); ?>/core/admin/style.css"/>		<!-- Concerto Framework Files -->		<?php	}		public function menu() {		global $menu;		$menu[99] = array('', 'read', 'separator-concerto', '', 'wp-menu-separator');			add_menu_page(__('General', 'concerto'), __('Concerto', 'concerto'), 'switch_themes', 'concerto_admin_menu', 'admin_general', null, 100);		add_submenu_page('concerto_admin_menu', __('General', 'concerto'), __('General', 'concerto'), 'switch_themes', 'concerto_admin_menu', 'admin_general');		add_submenu_page('concerto_admin_menu', __('Design', 'concerto'), __('Design', 'concerto'), 'switch_themes', 'concerto_admin_design', 'admin_design');		add_submenu_page('concerto_admin_menu', __('Tools', 'concerto'), __('Tools', 'concerto'), 'switch_themes', 'concerto_admin_tools', 'admin_tools');		add_submenu_page('concerto_admin_menu', __('Support', 'concerto'), __('Support', 'concerto'), 'switch_themes', 'concerto_admin_support', 'admin_support');	}}?>