<?php/*! * Concerto 1.0 beta * http://themeconcert.com/concerto * * [WARNING] * This is restricted file and should not be modified in any way(unless you know what * you are doing). This file serves as the Base Class for setting up administration pages. */class ConcertoAdmin {	public function __construct() {		//Require the individual Administration Pages		require_once CONCERTO_ADM . 'general.php';		require_once CONCERTO_ADM . 'design.php';		require_once CONCERTO_ADM . 'tools.php';		require_once CONCERTO_ADM . 'support.php';				//Bind Concerto to Wordpress		add_action('admin_init', array($this, 'init'));		add_action('admin_head', array($this, 'head'));		add_action('admin_menu', array($this, 'menu'));	}		public function init() {		$stylesheet_directory = get_bloginfo('stylesheet_directory');		wp_enqueue_script('jQueryMasonry', $stylesheet_directory . '/core/scripts/jquery.masonry.js', null, CONCERTO_VERSION);				// We Register Options		$this->registerSettings();	}		public function head() {		?>		<!-- Concerto Framework Files -->		<link rel="stylesheet" type="text/css" href="<?php bloginfo('stylesheet_directory'); ?>/core/admin/style.css"/>		<!-- Concerto Framework Files -->		<?php	}		public function menu() {		global $menu;		$menu[99] = array('', 'read', 'separator-concerto', '', 'wp-menu-separator');			add_menu_page(__('General', 'concerto'), __('Concerto', 'concerto'), 'switch_themes', 'concerto_admin_menu', 'admin_general', null, 100);		add_submenu_page('concerto_admin_menu', __('Concerto - General', 'concerto'), __('General', 'concerto'), 'switch_themes', 'concerto_admin_menu', 'admin_general');		add_submenu_page('concerto_admin_menu', __('Concerto - Design', 'concerto'), __('Design', 'concerto'), 'switch_themes', 'concerto_admin_design', 'admin_design');		add_submenu_page('concerto_admin_menu', __('Concerto - Tools', 'concerto'), __('Tools', 'concerto'), 'switch_themes', 'concerto_admin_tools', 'admin_tools');		add_submenu_page('concerto_admin_menu', __('Concerto - Support', 'concerto'), __('Support', 'concerto'), 'switch_themes', 'concerto_admin_support', 'admin_support');	}		/**	 * We Register configuration through Context	 */	public function registerSettings() {		register_setting('concerto_general', 'concerto_stage');		register_setting('concerto_general', 'concerto_general_site_title');		register_setting('concerto_general', 'concerto_general_favicon');		register_setting('concerto_general', 'concerto_general_homepage_description');		register_setting('concerto_general', 'concerto_general_homepage_keywords');		register_setting('concerto_general', 'concerto_general_syndication_url');		register_setting('concerto_general', 'concerto_general_scripts_head');		register_setting('concerto_general', 'concerto_general_scripts_footer');		register_setting('concerto_general', 'concerto_general_scripts_libraries_jquery');		register_setting('concerto_general', 'concerto_general_scripts_libraries_jquery_ui');		register_setting('concerto_general', 'concerto_general_menu');		register_setting('concerto_general', 'concerto_general_menu_use_pages');		register_setting('concerto_general', 'concerto_general_menu_pages_items');		register_setting('concerto_general', 'concerto_general_menu_use_categories');		register_setting('concerto_general', 'concerto_general_menu_categories_items');		register_setting('concerto_general', 'concerto_general_menu_use_tags');		register_setting('concerto_general', 'concerto_general_menu_tags_items');		register_setting('concerto_general', 'concerto_general_menu_show_home');		register_setting('concerto_general', 'concerto_general_menu_show_feed');		register_setting('concerto_general', 'concerto_general_language');		register_setting('concerto_general', 'concerto_seo_enable_canonical');		register_setting('concerto_general', 'concerto_extensions_thumbnail_enabled');		register_setting('concerto_general', 'concerto_seo_child_noindex');		register_setting