<div id="comments"><?php if (post_password_required()) { ?>	<p class="nopassword">This post is password protected. Enter the password to view any comments.</p></div><?php		return;	}?><?php if (have_comments()) { ?>	<?php		do_action('concerto_hook_before_commentlist');		do_action('concerto_hook_commentlist');		do_action('concerto_hook_after_commentlist');	} else {		if (!comments_open()) {		?>			<p class="nocomments">Comments are closed.</p>		<?php		}	}// Wordpress native Comment form methodcomment_form();?></div>