<div id="comments"><?php if (post_password_required()) { ?>	<p class="nopassword">This post is password protected. Enter the password to view any comments.</p></div><?php		return;	}?><?php if (have_comments()) { ?>	<h3 id="comments-title"><?php printf( _n( 'One Response to %2$s', '%1$s Responses to %2$s', get_comments_number(), 'concerto' ), number_format_i18n( get_comments_number() ), '<em>' . get_the_title() . '</em>' ); ?></h3><?php if (get_comment_pages_count() > 1 && get_option('page_comments')) { ?>	<div class="navigation">		<div class="nav-previous"><?php previous_comments_link('<span class="meta-nav">&larr;</span> Older Comments'); ?></div>		<div class="nav-next"><?php next_comments_link('Newer Comments <span class="meta-nav">&rarr;</span>'); ?></div>	</div><?php } ?><?php do_action('concerto_before_commentlist'); ?>	<ol class="commentlist">		<?php wp_list_comments(array('callback' => 'concerto_comment')); ?>	</ol><?php do_action('concerto_after_commentlist'); ?><?php if (get_comment_pages_count() > 1 && get_option('page_comments')) { ?>	<div class="navigation">		<div class="nav-previous"><?php previous_comments_link('<span class="meta-nav">&larr;</span> Older Comments'); ?></div>		<div class="nav-next"><?php next_comments_link('Newer Comments <span class="meta-nav">&rarr;</span>'); ?></div>	</div><?php }} else {	if (!comments_open()) {?>	<p class="nocomments">Comments are closed.</p><?php	}}comment_form();?></div><?phpfunction concerto_comment( $comment, $args, $depth ) {	$GLOBALS['comment'] = $comment;	switch ($comment->comment_type) {		case '':	?>	<li <?php comment_class(); ?> id="li-comment-<?php comment_ID(); ?>">		<?php do_action('concerto_before_comment'); ?>		<div id="comment-<?php comment_ID(); ?>">		<div class="comment-author vcard">			<?php do_action('concerto_comment_vcard'); ?>			<?php echo get_avatar($comment, 40); ?>			<?php printf('%s <span class="says">says:</span>', sprintf( '<cite class="fn">%s</cite>', get_comment_author_link())); ?>		</div>		<?php if ($comment->comment_approved == '0') { ?>			<em>Your comment is awaiting moderation.</em>			<br />		<?php } ?>		<div class="comment-meta commentmetadata">			<?php do_action('concerto_comment_metadata'); ?>			<a href="<?php echo esc_url(get_comment_link($comment->comment_ID)); ?>">			<?php printf('%1$s at %2$s', get_comment_date(),  get_comment_time()); ?></a><?php edit_comment_link('(Edit)', ' '); ?>		</div>		<div class="comment-body">			<?php do_action('concerto_comment_body'); ?>			<?php comment_text(); ?>		</div>		<div class="reply">			<?php comment_reply_link(array_merge($args, array( 'depth' => $depth, 'max_depth' => $args['max_depth']))); ?>		</div>		<?php do_action('concerto_after_comment'); ?>	</div>	<?php		break;		case 'pingback':		case 'trackback':	?>	<li class="post pingback">		<?php do_action('concerto_comment_pingback'); ?>		<p>Pingback: <?php comment_author_link(); ?><?php edit_comment_link('(Edit)', ' ' ); ?></p>	<?php		break;	}}?>