<section id="comments"><?php if (post_password_required()) { ?>	<p class="nopassword">This post is password protected. Enter the password to view any comments.</p></section><?php		return;	}?><?php if (have_comments()) { ?>	<h3 id="comments-title"><?php printf(_n('One Response to %2$s', '%1$s Responses to %2$s', get_comments_number()), number_format_i18n(get_comments_number()), '<em>' . get_the_title() . '</em>'); ?></h3>	<?php		do_action('concerto_before_commentlist');		do_action('concerto_commentlist');		do_action('concerto_after_commentlist');	} else {		if (!comments_open()) {	?>	<p class="nocomments">Comments are closed.</p>	<?php	}}// Wordpress native Comment form methodcomment_form();?></section>